    // }
    // if(choiceC){
    //     choiceC.addEventListener("click", output, true)
 
    // }
    // if(choiceD){
    //     choiceD.addEventListener("click", output, true)
 
    // }



    function shuffleArray() {
        for (var i = questionNumbers.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = questionNumbers[i];
            questionNumbers[i] = questionNumbers[j];
            questionNumbers[j] = temp;
        }

        for (let i = 0; i < 30; i++){
            quizOrder.push(questionNumbers[i])
        }

        
    }



    useEffect(() => {

        shuffleArray();
        setIsLoading(false)
        
    },[])

    useEffect(() => {
        // console.log(currentChoice)
    },[currentChoice])


    const handleQuestionChange = () => {

        if ('choiceA' == JSON.stringify(questions[quizOrder[counter].correctChoice])) {
            console.log('You made the Correct Choice!')
            points += 1
        } else {
            console.log('someonething went wrong!')
        }

        counter += 1
        console.log(counter)
        setTriggerChange(!triggerChange)

        if (counter == 29) {
            setTestCompleted(true)
        }


    }

