    }


  return (
    <div className='quiz-question-container'>

        <h2>{isLoading ? 'loading question...' : questions[quizOrder[counter]].question}</h2>
        <button id='choiceA' value='choiceA' onClick={() => {
            setCurrentChoice(choiceA.value)
            // console.log(questions[quizOrder[counter]].correctChoice + choiceA.value)
            
            // if ('choiceA' == JSON.stringify(questions[quizOrder[counter].correctChoice])) {
            //     console.log('You made the Correct Choice!')
            // } else {
            //     console.log('someonething went wrong!')
            // }
            // console.log(currentChoice)
        }}>{isLoading ? 'loading choice...' : questions[quizOrder[counter]].choiceA}</button>

        <button id='choiceB' value='choiceB'>{isLoading ? 'loading choice...' : questions[quizOrder[counter]].choiceB}</button>
        <button id='choiceC' value='choiceC'>{isLoading ? 'loading choice...' : questions[quizOrder[counter]].choiceC}</button>
        <button id='choiceD' value='choiceD'>{isLoading ? 'loading choice...' : questions[quizOrder[counter]].choiceD}</button>

        {testCompleted ? <button><a href='/completed'>Finish Quiz</a></button> : <button onClick={handleQuestionChange}>Next Question</button>}

    </div>
  )
}

export default Quiz
